mapper.select:

select following 
	from (select following, rownum as rn from follow where follower = #{follower}) 
	where rn >= #{FollowingLocalPageFirstNum} 
	and rn <= #{FollowingLocalPageLastNum}


select follower 
	from (select follower, rownum as rn from follow where following = #{following}) 
	where rn >= #{FollowerLocalPageFirstNum} 
	and rn <= #{FollowerLocalPageLastNum}
